import React, { useState, useEffect } from "react";
import "./home.css";
import { FLASK_URL } from "../Common";

export default function Home() {
  const Username = "user";
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(FLASK_URL + "/home", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(Username),
      });
    } catch (error) {
      console.error("Error:", error);
    }
  };
  const [cardsData, setCardsData] = useState([]);

  useEffect(() => {
    // Fetch data from Flask backend
    const fetchData = async () => {
      try {
        const response = await fetch("http://your-flask-api-endpoint");
        const data = await response.json();
        setCardsData(data); // Assuming data is an array of card objects
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []); // Empty dependency array ensures the effect runs only once on component mount

  return (
    <>
      <div className="container">
        <div className="left">
          <div className="Users-Courses">
            <div className="User-Course-Heading"></div>
            <div class="form-check form-switch">
              <input
                class="form-check-input"
                type="checkbox"
                role="switch"
                id="flexSwitchCheckDefault"
              />
              <label class="form-check-label" for="flexSwitchCheckDefault">
                Default switch checkbox input
              </label>
            </div>
            <div class="form-check form-switch">
              <input
                class="form-check-input"
                type="checkbox"
                role="switch"
                id="flexSwitchCheckChecked"
              />
              <label class="form-check-label" for="flexSwitchCheckChecked">
                Checked switch checkbox input
              </label>
            </div>
          </div>
          <div className="right">
            <h2>Right Container</h2>
            {cardsData.map((card, index) => (
              <div key={index} className="card text-center">
                <div className="card-header">{card.courseID}</div>
                <div className="card-body">
                  <h5 className="card-title">{card.assignmentID}</h5>
                  <p className="card-text">{card.description}</p>
                  <a href="#" className="btn btn-primary">
                    Go somewhere
                  </a>
                </div>
                <div className="card-footer text-body-secondary">
                  {card.timestamp}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}
